mapper.select:
select 
from goodsincoming
where incoming_num = #{incomingNum} and goods_num = #{goodsNum}

mapper.update:
update goodsIncoming
set incoming_Date = #{incomingDate}, incoming_Qty = #{incomingQty}
    , production_Date = #{incomingNum} , incoming_Price = #{incomingPrice}
where incoming_Num = #{incomingNum} and goods_Num = #{goodsNum}
